const container = document.querySelector('.container');
const seats = document.querySelectorAll('.row .seat:not(.occupied)');
const count = document.getElementById('count');
const total = document.getElementById('total');
const movieSelect = document.getElementById('movie');
const movieImg = document.getElementById('movieImg');

populateUI();

// + is for converting the movieSelect.value from string to number
let ticketPrice = +movieSelect.value;

// Update total and count
function updateSelectedCount() {
  const selectedSeats = document.querySelectorAll('.row .seat.selected');
  const selectedSeatsCount = selectedSeats.length;

  const seatsIndex = [...selectedSeats].map((seat) => [...seats].indexOf(seat));

  localStorage.setItem('selectedSeats', JSON.stringify(seatsIndex));

  count.innerText = selectedSeatsCount;
  total.innerText = selectedSeatsCount * ticketPrice;
}

// Save selected movie index and price
function setMovieData(movieIndex, moviePrice) {
  localStorage.setItem('selectedMovieIndex', movieIndex);
  localStorage.setItem('selectedMoviePrice', moviePrice);
}

function setMovieImg(index) {
  imgs = {
    0: 'https://images-na.ssl-images-amazon.com/images/I/81o%2BzjZ4KHL._SY741_.jpg',
    1: 'https://m.media-amazon.com/images/M/MV5BNGVjNWI4ZGUtNzE0MS00YTJmLWE0ZDctN2ZiYTk2YmI3NTYyXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_.jpg',
    2: 'data:image/jpeg;base64,',
    3: 'data:image/jpeg;base64,',
  };

  for (const img in imgs) {
    if (parseInt(img) === index) {
      localStorage.setItem('moviePoster', imgs[img]);
      movieImg.src = `${imgs[img]}`;
    }
  }
}

// Get the data from localstorage and populate the UI
function populateUI() {
  const selectedSeats = JSON.parse(localStorage.getItem('selectedSeats'));
  if (selectedSeats !== null && selectedSeats.length > 0) {
    seats.forEach((seat, index) => {
      if (selectedSeats.indexOf(index) > -1) {
        seat.classList.add('selected');
      }
    });
  }

  const selectedMovieIndex = localStorage.getItem('selectedMovieIndex');
  if (selectedMovieIndex !== null) {
    movieSelect.selectedIndex = selectedMovieIndex;
  }

  const moviePoster = localStorage.getItem('moviePoster');

  if (moviePoster !== null) {
    movieImg.src = `${moviePoster}`;
  }
}

// seat click evenet listener
container.addEventListener('click', (e) => {
  if (
    e.target.classList.contains('seat') &&
    !e.target.classList.contains('occupied')
  ) {
    e.target.classList.toggle('selected');
    updateSelectedCount();
  }
});

// update total eventlistener
movieSelect.addEventListener('change', (e) => {
  ticketPrice = +e.target.value;

  setMovieData(e.target.selectedIndex, e.target.value);
  setMovieImg(e.target.selectedIndex);

  updateSelectedCount();
});

updateSelectedCount();
